package com.uc.uccentergateway.config;import com.netflix.zuul.ZuulFilter;import com.netflix.zuul.context.RequestContext;import com.netflix.zuul.exception.ZuulException;import org.apache.commons.lang.StringUtils;import org.springframework.context.annotation.Configuration;import org.springframework.stereotype.Component;import javax.servlet.http.HttpServletRequest;/** * @author 9527 * @ClassName CustomZuulFilter * @Date 2020/1/15 * @Description TODO * @Version 1.0 */@Componentpublic class CustomZuulFilterPre extends ZuulFilter {    @Override    public String filterType() {        return "pre";    }    @Override    public int filterOrder() {        return 0;    }    @Override    public boolean shouldFilter() {        return true;    }    @Override    public Object run() throws ZuulException {        RequestContext requestContext = RequestContext.getCurrentContext();        HttpServletRequest request = requestContext.getRequest();        String secretKey = request.getHeader("secretKey");        if(StringUtils.isBlank(secretKey)){            Throwable throwable = new Throwable("unsafe request:secret key is empty");            throw new ZuulException(throwable,401,"unsafe request:secret key is empty");        }        return null;    }}